<?phpabstract class Model {    protected $db;    function __construct(array $attr = []) {        if (!defined('DB_SETTINGS')) Core::exception('DB : missed settings');        if (!isset(DB_SETTINGS['db_hostname'])) Core::exception('DB : missed hostname');        if (!isset(DB_SETTINGS['db_username'])) Core::exception('DB : missed username');        if (!isset(DB_SETTINGS['db_password'])) Core::exception('DB : missed password');        if (!isset(DB_SETTINGS['db_name']))     Core::exception('DB : missed dbname');        if (!isset(DB_SETTINGS['db_port']))     Core::exception('DB : missed dbport');        $db_charset  = DB_SETTINGS['db_charset'] ?? 'utf8';        $db_hostname = DB_SETTINGS['db_hostname'];        $db_username = DB_SETTINGS['db_username'];        $db_password = DB_SETTINGS['db_password'];        $db_name     = DB_SETTINGS['db_name'];        $db_port     = DB_SETTINGS['db_port'];        try {            $this->db = new PDO("mysql:host=".$db_hostname.";port=".$db_port.";dbname=".$db_name.";charset=".$db_charset, $db_username, $db_password);        } catch (PDOException $Exception) {            echo "mysql:host=".$db_hostname.";port=".$db_port.";dbname=".$db_name.";charset=".$db_charset;            Core::exception('PDO : ' . $Exception->getMessage() . ' (CODE:' . $Exception->getCode() . ')');        }    }}