<?phpabstract class Model {	protected $db;		function __construct ($attr = []) {		if (!isset($attr["hostname"])) Functions::error('db connect : wrong hostname');		if (!isset($attr["dbname"]))   Functions::error('db connect : wrong dbname');		if (!isset($attr["username"])) Functions::error('db connect : wrong username');		if (!isset($attr["password"])) Functions::error('db connect : wrong password');				$this->db = new PDO("mysql:host=".$attr["hostname"].";dbname=".$attr["dbname"], $attr["username"], $attr["password"]);		$this->db->query("SET NAMES 'utf8'");	}}