<?phpabstract class Model{    protected $db;    function __construct(array $attr = []) {        if (!isset($attr["hostname"])) Functions::error('db connect : missed hostname');        if (!isset($attr["dbname"])) Functions::error('db connect : missed dbname');        if (!isset($attr["username"])) Functions::error('db connect : missed username');        if (!isset($attr["password"])) Functions::error('db connect : missed password');        $this->db = new PDO("mysql:host=" . $attr["hostname"] . ";dbname=" . $attr["dbname"], $attr["username"], $attr["password"]);        $this->db->query("SET NAMES 'utf8'");    }}